mapper.select:
select n.* from (SELECT a.*, b. NAME AS class_name, c. NAME AS level_name, d. NAME AS province, e. NAME AS city, f. NAME AS county, g.realname AS nurse_name, h.realname AS customer_name, d1.name as age, d2.name as ability, d3.name as experience, d4.name as education, d5.name as price FROM needs a LEFT JOIN classes b ON a.class_id = b.id LEFT JOIN levels c ON a.level_id = c.id LEFT JOIN province d ON a.province_code = d.province_code LEFT JOIN city e ON a.city_code = e.city_code LEFT JOIN area f ON a.county_code = f.area_code LEFT JOIN users g ON a.nurse_id = g.id LEFT JOIN users h ON a.customer_id = h.id left join dictionary d1 on a.age_id=d1.id left join dictionary d2 on a.ability_id=d2.id left join dictionary d3 on a.experience_id=d3.id left join dictionary d4 on a.education_id=d4.id left join dictionary d5 on a.price_id=d5.id
a.id not in (#{idStr})
) n
n.id=#{id}
and n.customer_id=#{customerId}
and (n.age_id=#{ageId} or n.age="不限")
and (n.education_id=#{educationId} or n.education="不限")
and (n.experience_id=#{experienceId} or n.experience="不限")
and (n.ability_id=#{abilityId} or n.ability="不限")
and (n.class_id=#{classId} or n.class_name="不限")
and (n.level_id=#{levelId} or n.level_name="不限")
and n.province_code=#{provinceCode}
and n.city_code=#{cityCode}
and n.county_code=#{countyCode}
and n.status=#{status}
and n.is_enable=#{isEnable}
and 1=1 or n.nurse_id=#{nurseId}

select n.* from needs n


		nurse_id = #{nurseId}


		and status = #{status}

mapper.update:
update needs a set
status=#{status},nurse_id=#{nurseId}

where a.id=#{id}
